import React from 'react';
import { Card, Button } from 'react-bootstrap';

const EventCard = ({ event, onRegister }) => {
  // Fallback image if the event image fails to load
  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/300x200?text=Event+Image+Not+Found';
  };

  return (
    <Card className="event-card">
      <Card.Img
        variant="top"
        src={event.image || 'https://via.placeholder.com/300x200?text=Event+Image+Not+Found'}
        alt={event.name}
        onError={handleImageError}
      />
      <Card.Body>
        <Card.Title>{event.name}</Card.Title>
        <Card.Text><strong>Date:</strong> {new Date(event.date).toLocaleString()}</Card.Text>
        <Card.Text><strong>Location:</strong> {event.location}</Card.Text>
        <Card.Text>{event.description}</Card.Text>
        <Button variant="primary" onClick={onRegister}>Register</Button>
      </Card.Body>
    </Card>
  );
};

export default EventCard;
