import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Navbar, Nav, Container, Row, Col, Button, Card } from 'react-bootstrap';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import EventCard from './components/EventCard';
import './App.css';

const Home = () => (
  <>
    {/* Hero Section */}
    <section className="hero text-center">
      <Container className="py-5">
        <h1 className="display-3 mb-4 animate-fade-in">Welcome to Dynamics 360 Events</h1>
        <p className="lead mb-4 animate-slide-up">Discover, connect, and celebrate local events with ease.</p>
        <Button as={Link} to="/events" variant="primary" size="lg" className="animate-pop-in">
          Explore Events
        </Button>
      </Container>
    </section>

    {/* About Us Section */}
    <section className="about py-5 bg-light">
      <Container>
        {/* About Us Heading */}
        <h2 className="text-center mb-5 animate-fade-in">About Dynamics 360</h2>
        
        {/* Our Story */}
        <Row className="align-items-center mb-5">
          <Col md={6} className="animate-slide-right">
            <h3 className="mb-3">Our Story</h3>
            <p className="lead text-muted">
              Dynamics 360 was founded with a vision to bring communities together through memorable events. 
              Based in Islamabad, Pakistan, we’re a passionate team dedicated to connecting people with local experiences 
              that inspire and entertain.
            </p>
          </Col>
          <Col md={6} className="text-center animate-slide-left">
            <div className="about-image animate-zoom-in">
              <img src="https://images.unsplash.com/photo-1507608869274-7d71f9557d48?q=80&w=2070&auto=format&fit=crop" alt="Our Story" className="img-fluid rounded shadow" />
            </div>
          </Col>
        </Row>

        {/* Our Mission */}
        <Row className="align-items-center mb-5">
          <Col md={6} className="order-md-2 animate-slide-left">
            <h3 className="mb-3">Our Mission</h3>
            <p className="lead text-muted">
              We aim to make event discovery seamless and engaging, fostering connections that enrich lives. 
              Whether it’s a music festival or a tech meetup, we’re here to help you find your next unforgettable experience.
            </p>
          </Col>
          <Col md={6} className="text-center order-md-1 animate-slide-right">
            <div className="about-image animate-zoom-in">
              <img src="https://images.unsplash.com/photo-1522202176988-66273c2fd55f?q=80&w=2071&auto=format&fit=crop" alt="Our Mission" className="img-fluid rounded shadow" />
            </div>
          </Col>
        </Row>

        {/* Our Team */}
        <Row className="text-center">
          <Col>
            <h3 className="mb-4 animate-fade-in">Meet Our Team</h3>
          </Col>
        </Row>
        <Row>
          {[
            { name: "Ayesha Khan", role: "Founder & CEO", img: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?q=80&w=1887&auto=format&fit=crop" },
            { name: "Usama Malik", role: "Event Manager", img: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?q=80&w=1887&auto=format&fit=crop" },
            { name: "Sara Ahmed", role: "Marketing Lead", img: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?q=80&w=1887&auto=format&fit=crop" }
          ].map((member, index) => (
            <Col md={4} key={index} className="mb-4 animate-slide-up">
              <Card className="team-card shadow-sm">
                <Card.Img variant="top" src={member.img} alt={member.name} className="rounded-circle mx-auto mt-3 team-img" />
                <Card.Body>
                  <Card.Title>{member.name}</Card.Title>
                  <Card.Text className="text-muted">{member.role}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  </>
);

// [Rest of the code for Contact, Events, and App remains the same as in the previous response]

function App() {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);

  useEffect(() => {
    console.log('API URL:', process.env.REACT_APP_API_URL);
    axios.get(`${process.env.REACT_APP_API_URL}/events`)
      .then(response => {
        console.log('Fetched events:', response.data);
        if (Array.isArray(response.data)) {
          setEvents(response.data);
          setFilteredEvents(response.data);
        } else {
          console.error('Data is not an array:', response.data);
          setEvents([]);
          setFilteredEvents([]);
        }
      })
      .catch(error => {
        console.error('Error fetching events:', error);
        if (error.response) {
          console.log('Response data:', error.response.data);
          console.log('Response status:', error.response.status);
        } else if (error.request) {
          console.log('No response received:', error.request);
        } else {
          console.log('Error setting up request:', error.message);
        }
      });
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = events.filter(event =>
      event.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    console.log('Filtered events:', filtered);
    setFilteredEvents(filtered);
  };

  const handleDateFilter = () => {
    const today = new Date('2025-05-01T00:00:00Z'); // Test date to show all events
    const upcomingEvents = events.filter(event => {
      const eventDate = new Date(event.date);
      return eventDate >= today;
    });
    console.log('Upcoming events:', upcomingEvents);
    setFilteredEvents(upcomingEvents);
  };

  const handleRegister = () => {
    alert('Registration feature simulated!');
  };

  return (
    <Router>
      <Navbar bg="light" expand="lg" className="shadow-sm sticky-top">
        <Container>
          <Navbar.Brand as={Link} to="/">
            <img src="/logo.png" alt="Dynamics 360 Logo" height="40" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link as={Link} to="/" className="me-3">Home</Nav.Link>
              <Nav.Link as={Link} to="/events" className="me-3">Events</Nav.Link>
              <Nav.Link as={Link} to="/contact">Contact</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/events" element={
          <Events
            events={events}
            filteredEvents={filteredEvents}
            setFilteredEvents={setFilteredEvents}
            handleRegister={handleRegister}
            handleSearch={handleSearch}
            handleDateFilter={handleDateFilter}
          />
        } />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </Router>
  );
}

export default App;
