import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Navbar, Nav, Container, Row, Col, Button } from 'react-bootstrap';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import EventCard from './components/EventCard';
import './App.css';

const Home = () => (
  <section className="hero text-center">
    <Container>
      <h1 className="display-4 mb-4">Welcome to Dynamics 360 Events</h1>
      <p className="lead mb-4">Discover and join exciting events near you with ease.</p>
      <Button as={Link} to="/events" variant="primary" size="lg">
        Explore Events
      </Button>
    </Container>
  </section>
);

const Contact = () => (
  <section className="contact text-center py-5">
    <Container>
      <h2 className="mb-4">Contact Us</h2>
      <p>Email: <a href="mailto:hr@dynamics360.net">hr@dynamics360.net</a></p>
      <p>Phone: +92 339 411 1994</p>
      <p>Address: 404, 5th Floor, Golden Heights, Business Square, Block C, Gulberg Greens, Islamabad, Pakistan</p>
    </Container>
  </section>
);

const Events = ({ events, filteredEvents, setFilteredEvents, handleRegister, handleSearch, handleDateFilter }) => {
  console.log('Rendering Events, filteredEvents:', filteredEvents);
  return (
    <>
      <section className="hero text-center">
        <Container>
          <h1 className="display-4">Discover Events Near You</h1>
        </Container>
      </section>
      <section className="events py-5">
        <Container>
          <h2 className="text-center mb-4">Featured Events</h2>
          <SearchBar onSearch={handleSearch} onDateFilter={handleDateFilter} />
          <Row>
            {filteredEvents.length > 0 ? (
              filteredEvents.map(event => (
                <Col md={4} key={event._id} className="mb-4">
                  <EventCard event={event} onRegister={() => handleRegister(event._id)} />
                </Col>
              ))
            ) : (
              <p className="text-center">No events found. Check console for errors.</p>
            )}
          </Row>
        </Container>
      </section>
    </>
  );
};

function App() {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);

  useEffect(() => {
    console.log('API URL:', process.env.REACT_APP_API_URL);
    axios.get(`${process.env.REACT_APP_API_URL}/events`)
      .then(response => {
        console.log('Fetched events:', response.data);
        if (Array.isArray(response.data)) {
          setEvents(response.data);
          setFilteredEvents(response.data);
        } else {
          console.error('Data is not an array:', response.data);
          setEvents([]);
          setFilteredEvents([]);
        }
      })
      .catch(error => {
        console.error('Error fetching events:', error);
        if (error.response) {
          console.log('Response data:', error.response.data);
          console.log('Response status:', error.response.status);
        } else if (error.request) {
          console.log('No response received:', error.request);
        } else {
          console.log('Error setting up request:', error.message);
        }
      });
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = events.filter(event =>
      event.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    console.log('Filtered events:', filtered);
    setFilteredEvents(filtered);
  };

  const handleDateFilter = () => {
    const today = new Date('2025-05-01T00:00:00Z'); // Test date to show all events
    const upcomingEvents = events.filter(event => {
      const eventDate = new Date(event.date);
      return eventDate >= today;
    });
    console.log('Upcoming events:', upcomingEvents);
    setFilteredEvents(upcomingEvents);
  };

  const handleRegister = () => {
    alert('Registration feature simulated!');
  };

  return (
    <Router>
      <Navbar bg="light" expand="lg" className="shadow-sm">
        <Container>
          <Navbar.Brand as={Link} to="/">
            <img src="/logo.png" alt="Dynamics 360 Logo" height="40" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link as={Link} to="/" className="me-3">Home</Nav.Link>
              <Nav.Link as={Link} to="/events" className="me-3">Events</Nav.Link>
              <Nav.Link as={Link} to="/contact">Contact</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/events" element={
          <Events
            events={events}
            filteredEvents={filteredEvents}
            setFilteredEvents={setFilteredEvents}
            handleRegister={handleRegister}
            handleSearch={handleSearch}
            handleDateFilter={handleDateFilter}
          />
        } />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </Router>
  );
}

export default App;
